mapper.select:
SELECT
    *


     FROM
         MC_IN_PAIENT


     FROM
         MC_OUT_PAIENT




         AND PAT_NO      = #{patNo}


         AND DISEASE_CD  = #{diseaseCd}


         AND DISEASE_NM  = #{diseaseNm}


         AND PRESC_GUBUN = #{prescGubun}


         AND PRESC_DATE BETWEEN #{startDay} AND #{endDay}

mapper.insert:
INSERT INTO
    MC_IN_PAIENT (
        PAT_NO
      , PRESC_DATE
      , DISEASE_CD
      , DISEASE_NM
      , DEPT
      , HOSPTLZ_RECEIPT_NO
      , FEE_CD
      , FEE_NM
      , BIG_GRP
      , MID_GRP
      , SM_GRP
      , COST
      , UNIQUE_NTC
      , PRESC_GUBUN
      , DSCG_GUBUN
      , DSCG_DATE
      , PRESC_NO
      , DOCT_NM
    )
VALUES (
    #{patNo}
  , #{prescDate}
  , #{diseaseCd}
  , #{diseaseNm}
  , #{dept}
  , #{hosptlzReceiptNo}
  , #{feeCd}
  , #{feeNm}
  , #{bigGrp}
  , #{midGrp}
  , #{smGrp}
  , #{cost}
  , #{uniqueNtc}
  , #{prescGubun}
  , '0'
  , #{dscgDate}
  , #{prescNo}
  , #{doctNm}
)

mapper.update:
UPDATE
    MC_IN_PAIENT
SET
    PRESC_DATE         = #{prescDate}
  , DISEASE_CD         = #{diseaseCd}
  , DISEASE_NM         = #{diseaseNm}
  , DEPT               = #{dept}
  , HOSPTLZ_RECEIPT_NO = #{hosptlzReceiptNo}
  , FEE_CD             = #{feeCd}
  , FEE_NM             = #{feeNm}
  , BIG_GRP            = #{bigGrp}
  , MID_GRP            = #{midGrp}
  , SM_GRP             = #{smGrp}
  , COST               = #{cost}
  , UNIQUE_NTC         = #{uniqueNtc}
  , PRESC_GUBUN        = #{prescGubun}
  , DSCG_GUBUN         = #{dscgGubun}
  , DSCG_DATE          = #{dscgDate}
  , DOCT_NM            = #{doctNm}
WHERE
    PAT_NO             = #{patNo}
AND PRESC_NO           = #{prescNo}

