mapper.select:
SELECT SCD.CLINSPE_NO, SCD.DELIVERY_DATE, HE.EMP_NO, HE.EMP_NM, SC.EXMNT_CD, SC.CLINSPE_NM, SCR.EME_YN, SCR.CLINSPE_RECEIPT_DATE, SCR.CLINSPE_STATUS FROM SL_CLINSPE_DELIVERY SCD, HM_EMP HE, SL_CLINSPE SC, SL_CLINSPE_RECEIPT SCR WHERE SCD.DELIVERY_EMP=HE.EMP_NO AND SCD.CLINSPE_NO=SC.CLINSPE_NO AND SCD.CLINSPE_NO=SCR.CLINSPE_NO
AND SCD.DELIVERY_DATE BETWEEN #{startDate}
AND #{endDate}
AND SCR.EME_YN = #{emeYn}
AND SCR.CLINSPE_STATUS = #{clinspeStatus}

CALL PC_SP_DELIVERY_CLINSPE(
	#{clinspeNo, mode=IN, jdbcType=VARCHAR, javaType=string}
,	#{date, mode=IN, jdbcType=VARCHAR, javaType=string}
,	#{empNo, mode=IN, jdbcType=VARCHAR, javaType=string}
, 	#{errorCode, mode=OUT, jdbcType=VARCHAR, javaType=string}
, 	#{errorMsg, mode=OUT, jdbcType=VARCHAR, javaType=string}
)
