mapper.select:
SELECT R.CLINSPE_NO, R.CLINSPE_RECEIPT_DATE, R.RECEIPT_CD, R.EME_YN, C.RECEIPT_CANCEL_CAU, C.RECEIPT_CANCEL_DATE, S.DGNS_NM, S.EXMNT_CD, S.CLINSPE_NM, S.BLOODGET_DATE, S.BLOODGET_EMP FROM SL_CLINSPE S, SL_CLINSPE_RECEIPT R LEFT OUTER JOIN SL_CLINSPE_RECEIPT_CANCEL C ON R.CLINSPE_NO = C.CLINSPE_NO WHERE S.CLINSPE_NO = R.CLINSPE_NO AND R.CLINSPE_RECEIPT_DATE BETWEEN #{receiptStartDate} AND #{receiptEndDate}
AND R.EME_YN = #{emeYn}
AND R.RECEIPT_CD = #{receiptCd}

mapper.update:
UPDATE SL_CLINSPE_RECEIPT 
       SET CLINSPE_RECEIPT_DATE=#{clinspeReceiptDate},
       RECEIPT_CD=#{receiptCd},
       EME_YN=#{emeYn} 
 WHERE CLINSPE_NO=#{clinspeNo}

UPDATE SL_CLINSPE_RECEIPT_CANCEL 
       SET RECEIPT_CANCEL_CAU=#{receiptCancelCau},
       RECEIPT_CANCEL_DATE=#{receiptCancelDate} 
 WHERE CLINSPE_NO=#{clinspeNo}
