mapper.select:
SELECT *
FROM WB_HROOM


		AND WARD = #{ward}


		AND HROOM = #{hroom}


		AND GENDER = #{gender}

SELECT wr.HROOM_NO, wr.WARD, wr.HROOM, wh.HOSPTLZ_PLN_DATE, wh.HOSPTLZ_FINAL_DATE, wh.TRMT_DEPT_CD, wr.HROOM_GRADE, wh.PAT_NO, wr.GENDER, wr.SIBD_CNT, wr.PMSN_SIBD, wr.FINAN_CNT FROM WB_HROOM wr, WH_HOSPTLZ_RSVT wh WHERE wr.TRMT_DEPT=wh.TRMT_DEPT_CD AND wr.HROOM = wh.HOPE_ROOM
AND wr.WARD = #{ward}
AND wr.HROOM = #{hroom}
